const JWTValidator = require('@curity/jwt-validation');

const jwtValidator = (issuer, audience, allowed_jwt_algorithms, jwt_sig_public_key) => {
       return new JWTValidator(issuer, audience, allowed_jwt_algorithms, jwt_sig_public_key);
}

const getDefaultJWTValidator = (issuer, audience) => {
    const allowed_algorithms = ['RS256', 'ES384'];
    const jwt_sig_public_key = {
        format: 'metadata_url',
        value: issuer + '/.well-known/openid-configuration'
    }

    return jwtValidator(issuer, audience, allowed_algorithms, jwt_sig_public_key);
};

exports = module.exports = jwtValidator;
exports.jwtValidator = jwtValidator;
exports.getDefaultJWTValidator = getDefaultJWTValidator;
